svg.script:
'use strict';

function test_marker_value(shorthandValue, longhandValues) {
  const shorthandName = 'marker';
  const longhandNames = [
      'marker-start',
      'marker-mid',
      'marker-end'
  ];
  test_shorthand_value(shorthandName, shorthandValue, longhandNames, longhandValues);
}

test_marker_value('none', [
    'none',
    'none',
    'none'
]);

test_marker_value('url("https://example.com/")', [
    'url("https://example.com/")',
    'url("https://example.com/")',
    'url("https://example.com/")'
]);
